mapper.update:
UPDATE SPENDING SET


						P_NAME=#{p_name}

						P_NAME=NULL


				,


						P_DETAIL=#{p_detail}

						P_DETAIL=NULL


				,


						P_LOCATION=#{p_location}

						P_LOCATION=NULL


				,


						P_MONEY=#{p_money}

						P_MOENY=NULL


				,


						P_CASHABLE=#{p_cashable}

						P_CASHABLE=NULL


				,


						P_CARD=#{p_card}

						P_CARD=NULL


				,


						P_SAT=#{p_sat}

						P_SAT=NULL


				,


						P_NEED=#{p_need}

						P_NEED=NULL



WHERE P_SEQ=#{p_seq}

mapper.select:
SELECT DISTINCT ROUND(SUM(P_MONEY)/30) AS P_MONEY
FROM SPENDING
WHERE SUBSTR(TO_CHAR(P_REGDATE,'YYYY-MM'),1,7)=#{p_location} AND ID=#{id}
