mapper.update:
UPDATE SAVE SET 


						S_NAME=#{s_name}

						S_NAME=NULL


				,


						S_MONEY=#{s_money}

						S_MOENY=NULL


				,


						S_TAX=#{s_tax}

						S_TAX=NULL


				,


						S_STARTDATE=#{s_startdate}

						S_STARTDATE=NULL


				,


						S_ENDDATE#{s_enddate}

						S_ENDDATE=NULL


				,


						S_MEMO=#{s_memo}

						S_MEMO=NULL


WHERE S_SEQ=#{s_seq}
